export const lightTheme = {
  bgColor: '#f8f7f4',
  textColor: '#31302E',
  borderColor: '1px solid #eaeaea',
};

export const darkTheme = {
  bgColor: '#1E1E22',
  textColor: '#ccc',
  borderColor: '1px solid #2c2d33',
};

export const theme = {
  lightTheme,
  darkTheme,
};
